igation #issues, #navigation #issues .uk-icon {color: rgba(255, 255, 255, 0.5)!important;}#navigation #issues:hover, #navigation #issues:focus, #navigation #issues:hover .uk-icon, #navigation #issues:hover .uk-icon {color: rgba(255, 255, 255, 0.875)!important;}.rewrittenLink {word-wrap: break-word!important;}.user-avatar-wrapper {display: inline-block!important;}.user-avatar {!important;}.uk-card-default {box-shadow: rgb(224, 224, 224) 0px -1px 0px, rgba(0, 0, 0, 0.12) 0px 0px 2px, rgba(0, 0, 0, 0.24) 0px 2px 4px !important;}.skynet-label {position: absolute!important; left: 0px!important; transform: translate3d(-12px, -10px, 0px)!important; cursor: default!important; font-size: 0.675rem !important; padding-top: 2px !important; letter-spacing: 0.0675em !important;}.pulse {animation: pulse 1.5s infinite!important;}@keyframes pulse { 
