/*
LOADER
*/
.loader {
	background-color: #fff;
	display: flex;
	align-items: center;
	justify-content: center;
	height: 100vh;
	min-height: 100vh;
	width: 100vw;
	min-width: 100vw;
	position: fixed;
	top: 0; right: 0; bottom: 0; left: 0;
	z-index: 1000000;
	transition: opacity 600ms ease-in-out;
}

body:before {
    content: '';
    background-color: #fff;
	height: 100vh;
	min-height: 100vh;
	width: 100vw;
	min-width: 100vw;
	position: fixed;
	top: 0; right: 0; bottom: 0; left: 0;
	z-index: 100000;
    transition: opacity 600ms ease-in-out;
}

body.loading-finished:before {
    content: none;
}

.loader:before {
	content: '';
    border: 8px solid #f3f3f3;
    border-top: 8px solid #3498db;
	border-radius: 50%;
	width: 40px;
	height: 40px;
	animation: spin 600ms linear infinite;
}

@keyframes spin {
	0% {
		transform: rotate(0deg);
	}
	100% {
		transform: rotate(360deg);
	}
}

.loader.is-loading {
	opacity: 1;
}

.loader.is-finished {
	opacity: 0 !important;
}

.no-scroll {
	max-height: 100vh;
	overflow: hidden;
}


/* Prevent overflow-x on body */
body {
	background-color: #f2f4f8;
	overflow-x: hidden;
}

#login {
	height: 100vh;
}

#login-form {
